import React, { useEffect, useState } from "react";
import Instruction from "../Instructions/Instruction";
import Main from "../Main/Main";
import "./Form.css";

export const Form = () => {
  const [page, setPage] = useState(0);
  const [isSubmit, setIsSubmit] = useState(false);

  const FormTitles = ["Interaction Title", "Syestem Instructions :"];

  const validate = (values) => {
    const errors = {};
    if (!values.name) {
      errors.name = "Name is required!!";
    }
    if (!values.email) {
      errors.email = "Email is required!!";
    }
    if (!values.phone) {
      errors.phone = "Phone No. is required!!";
    }
    if (!values.access) {
      errors.access = "Access Code is required!!";
    }
    return errors;
  };
  const pageDisplay = () => {
    if (page === 0) {
      return <Main />;
    } else if (page === 1) {
      return <Instruction />;
    }
    console.log(page);
  };

  return (
    <div className="container">
      <h1>{FormTitles[page]}</h1>
      <form>
        <div className="form_div">{pageDisplay()}</div>
        <button
          className="field button black"
          onClick={() => isSubmit && setPage(1)}
        >
          Validate to Start
        </button>
      </form>
    </div>
  );
};
