import React, { useEffect, useState } from "react";
import Instruction from "../Instructions/Instruction";
import "./Form.css";

export const Form = () => {
  const initialValues = { name: "", email: "", phone: "", access: "" };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [show, setShow] = useState(false);
  const [isSubmit, setIsSubmit] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    if (!values.name) {
      errors.name = "Name is required!";
    }
    if (!values.email) {
      errors.email = "Email is required!";
    }
    if (!values.access) {
      errors.access = "Access Code is required!";
    }
    if (!values.phone) {
      errors.phone = "Phone No. is required! ";
    }

    return errors;
  };

  return (
    <div className="container" style={{ marginTop: "350px" }}>
      <form onSubmit={handleSubmit}>
        <div className="ui form_div">
          <h1>Interaction Title</h1>
          <div className="field">
            {/* <label>name</label> */}
            <input
              type="text"
              name="name"
              placeholder="Enter Your Name"
              value={formValues.name}
              onChange={handleChange}
            />
            <p>{formErrors.name}</p>
          </div>
          <div className="field">
            {/* <label>Email</label> */}
            <input
              type="text"
              name="email"
              placeholder="Enter Your Email"
              value={formValues.email}
              onChange={handleChange}
            />
            <p>{formErrors.email}</p>
          </div>
          <div className="field">
            {/* <label>Phone No.</label> */}
            <input
              type="text"
              name="phone"
              placeholder="Enter Your Phone Number"
              value={formValues.phone}
              onChange={handleChange}
            />
            <p>{formErrors.phone}</p>
          </div>
          <div className="field">
            {/* <label>Access Code</label> */}
            <input
              type="text"
              name="access"
              placeholder="Enter Your Access Code"
              value={formValues.access}
              onChange={handleChange}
            />
            <p>{formErrors.access}</p>
          </div>
          <button
            className="fluid ui button blue"
            onClick={() => isSubmit && setShow(true)}
          >
            Validate to Start
          </button>
          <div style={{ marginTop: "20px" }}>
            {show && (
              <div>
                <Instruction />
                <button style={{ marginTop: "15px" }}>Start Test</button>
              </div>
            )}
          </div>
        </div>
      </form>
    </div>
  );
};
